import axios from 'axios';
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function SearchProfile({ setUrlProfile, urlProfile }) {
  const [input, setInput] = useState('');
  const [emptyInput, setEmptyInput] = useState(true);
  const navigate = useNavigate();

  const getProfile = async () => {
    try {
      const promise = await axios.get(`https://api.github.com/users/${input}`);
      setUrlProfile(`https://api.github.com/users/${input}`);
      console.log(promise.data);
      navigate('/profile');
    } catch (error) {
      console.log(setUrlProfile);
      console.log(error.message);
    }
  };

  const verifyImput = () => {
    if (input === '') {
      return setEmptyInput(false);
    }
    return getProfile();
  };

  return (
    <main>
      <h1>Buscar Reposit칩rio no github</h1>
      <nav>
        <label htmlFor="inputProfile">
          <input
            id="inputProfile"
            placeholder="Digite o nome do usu치rio"
            onChange={(e) => setInput(e.target.value)}
          />
        </label>
        <button
          type="button"
          onClick={verifyImput}
        >
          Pesquisar
        </button>
        {!emptyInput && <h1>informe um nome de usu치rio v치lido do github</h1>}
      </nav>
    </main>
  );
}

export default SearchProfile;
